    Book1(
        number: "110",
        title: '‘ŌKETI',
        content:
            "SI’I HENGIHENGI Ē KUO ‘ALU KAPAKAU AI SI’ETE MANATU KI HE ‘ŌKETI FISI Ē HAHAU TAULEVA KI HE PĀ’ANGANGALU \n\n ‘ANGA’ANGA NI KUO ME’A NOA KA FALALA HE FUNGA HAU’ALOFA KI HO ‘IMISI MAKA KOLOA TUI FAKAHOLO KO HOTO KAHOA \n\n TA’ALO Ē MALU ‘I HE EFIAFI PEA TE HAHANU LAUNOA KI AI KE ‘ILOA MAI ‘ENE TUPULAKI ‘A E ‘OFA HE POUKEI LANGAKALI \n\n TAU SINO NI ‘APĒ KA MELIE LAU PEA KONA AI KI HA’O FEKAU KA NEONGO IA ‘E KALATIOLA NA’A KUO TA’ENGATA HA’ATA NI ‘OFA"),
    Book1(
        number: "111",
        title: 'TALI SOLA',
        content:
            "LOTO NE KAFO E MAUSA E FĀ FATAKI HE HUELO E LA’Ā VĀKAUNAHU MO ‘EKU ‘OFA KI HE MAHENI FAI FUOLOA \n\n LEA ‘A E PEAU ‘I HE EFIAFI SI’ENE VIKIA ‘A E LANU E LANGI ‘EVA MO LATA HOKU ‘ATAMAI HE LANGI MA’A E VAIKAU’AKI \n\n NE KAKAU SI’ETE FAKAHAHANO KI HE VAVĀ HE TAUMAMA’O MOSI HA ‘UHA HE LOUHINGANO KO E OLA’ANGA E PŌNGIA-‘I-VAO \n\n TAU NGALO ‘AFĒ NAI NGALO HE HĀ VE’EVE’E MO E PAPAI FĀ ‘OKU NGANGATU HE TALI SOLA FAKAMA’UNGA E TALANOA"),
    Book1(
        number: "112",
        title: 'KO E HĀ NAI HA TATAU',
        content:
            "KO E HĀ NAI HA TATAU KO E HĀ ‘A E TUHA ‘O E FIEFIA NE TU’U MO’UNGA HA NGOUE LILE ‘OKU TALAMUKA VĀNGANA ‘A E MATAVAI MAPUNA \n\n ONGO SI’I LE’O ‘O E TO’I LEA HE KAKALA ‘ENE FASI ‘I HE ‘EA MAFANA TO’I ‘O E HEILALA ‘ALAHA ‘A E MAPA FEAU TONU SI’OTO NĀPA’A \n\n SIOFIA AI PĒ FETU’U ‘EVA HE KANIVA ‘ASI HE VAVĀ PEA ‘AO’AOFIA MUIMUI LOTO ‘A E ‘OFA IA TATAU ‘A E ‘EA PEA MO E TAHI NA \n\n TAU UISA KUO MA’A MĀLIE E ‘AHO MA’ANU E MAFUA ‘I HE LOLOTO KAUKAU LA’Ā AI HOKU SINO HE MAUSA ‘O E VE’EVE’E HINGANO"),
    Book1(
        number: "113",
        title: 'TAHI MATE',
        content:
            "LOTO NE MAO TĒKINA HE VAVĀ HULUNGIA ‘E HE ‘OFA KEMO LEVA SI’ENE MĀFANA ‘O HOKO KO HA FU’U VAI ‘ATĀ \n\n TŌ ‘A E ‘UHA ‘I HE FUNGA MAUNITENI HIFO HE TELE’A MAMAHI VAITAFE AI KI HE ‘ŌSENI ‘O HOKO KO HA KONA ‘Ī’Ī \n\n KO SI’ETE FAKA’AMU NOA AI PĒ KE U FOKI HE KALĀVITE KA KUO TE KAU KITA ‘ENI ‘I HE LAVE ‘O TULUTĀ KI HE TAHI MATE \n\n KA NE KO HA MAKATU’U ENI KEU FOA PE HA KALAVANA KEU FONONGA KEHE KE U MA’ULUTA AI ‘A E ‘OFA KA KOLOTO, HE MAAMA NGA’AHOA \n\n TAU TAFE TOULIKI ‘A LO’IMATA HAHAU HO’O NGOUE KAKALA FAUNITENI TŌ ‘I HE KILISITALA FE’AO ‘I HO ‘EVE’EVA’ANGA"),
    Book1(
        number: "114",
        title: 'ANGI MAI SI’I MATANGI HE TONGA',
        content:
            "ANGI MAI SI’I MATANGI HE TONGA ‘O FALALA ‘I NUKU’ALOFA AKE LEVA SI’EKU MANATU LOLOMA KI HE SALUSALU FALAHOLA \n\n MALU EFIAFI ANGI IKI ‘O FAFANA HE HALA PAINI PEHEANGE MAI NA’E TOE TAIMI FUIFUI ‘ENA KUO MAHIKI \n\n LOUPUA FELUTENI HE HUA LOUHUNI HE ‘OTU MU’OMU’A SI’I HUNI E LOLO-‘A-HALAEVALU HE’IKAI TE KE NGALO ‘IATE AU \n\n TAU SOIFUA ‘OE, ‘O LA’U PELE SI’I HEILALA MO E SIALE LEIPUA ‘O E MALAMA NI TONGA HE ‘OIAUĒ! ‘ALU Ā KOE MO ‘EKU MANATU KA U NOFO MO E FAKAKAUKAU HE’IKAI NGALO KAE TU’U MA’U SI’I ‘OFA ATU"),
    Book1(
        number: "115",
        title: 'SI’I FISI’I LILIŌ',
        content:
            "SI’I FISI’I LILIŌ MATALA MASANI NE MAHEIKAU ‘O KA EFIAFI TUIFIO E HUNI PEA MO E LANGAKALI LŪPŪ ‘A E HEILALA ‘I TAVAHI FANGUNA E MAILE MEI PALASI KE TUI E FĀ KO HONO PAPAI \n\n NE NGA’AHOA E KAKALA ‘I HE FAKA’AMU ‘O TAUELE ‘I HE HALA SIU’ILIKUTAPU PEA LUKI ‘A LOFANITANI KI LO’ĀMANU MO FINEFEKAI KI FONUATANU KAE TALA ‘E HE MANU ‘OKU VĀTAU KO E FONOFONO KOULA ‘A HONO FAKAMA’U \n\n ‘OFA HE SIU ‘O E MANU TAUFONUA ‘O TU’ULA ‘I HE FĀ ‘I FANGALE’OUNGA PEA PULUSI KI HE MOTO ‘O E FUKA KE ANGINA ‘E HE FUNGA ‘ALAKIFONUA KAHOA E VE’EVE’E FĀ’ONELUA MOKOI E LAIONE ‘I MALA’EKULA \n\n TAU UISA HOTO LOTO KUO KĀVEA PEA LUVA E KAKALA HE Ī-‘O-LUPEA ‘OFA LOTO ‘OKU NOFO-‘I-LELENGA ‘OKU TANGI ‘I MATE-KA-KUO-‘EVA"),
    Book1(
        number: "116",
        title: 'MANAKO HE KUKUVALU',
        content:
            "MANAKO HE KUKUVALU HONO ‘EA NGANGATU PEA FA’AHIKEHEA TUKU LOTO ‘O ‘IKAI KE ‘EKEA HE FUNGANI SISI NA’E ALA LEA \n\n ‘O KA UALIKU LEVA E MATANGI TALANOA HUA HE HALA FATAI KĀVEA AI HOKU ‘ATAMAI UISA HE ‘OFA FŪFŪNAKI \n\n ‘EKU TOKI A’USIA ‘I HE ‘OFA NA’E TAKU KO E MATA’IKOLOA HE KOULA SIVI ‘O HAOHAOA LAU KO E MUNA E ME’A KOTOA \n\n TAU LEI PUA ‘O E TAUKANAVE KE U HIFO ATU KI HE FANGA-KO-FĒFĒ KI MAKA-‘A-KIU MO E SIALE PEA MO KĀPETI MAILE KO HOTO KAHOA FAKATĒTĒ ‘O TAPU KE LUVA TAPU KE VETE"),
    Book1(
        number: "117",
        title: 'HE ‘UHILA NE TAPA ‘I HE TONGA',
        content:
            "HE ‘UHILA NE TAPA ‘I HE TONGA ‘O FANGUNA E FAKALŌNGONOA ‘ISE’ISA KUO SIU ‘ETE ‘OFA PEA ‘ALA ‘I LOTO NUKU’ALOFA KO E FAKAMA’UNGA E TALANOA \n\n KUO AKE ‘EKU MANATU KAKALA LOTO MU’A MO E VAO HEILALA FALA MOHOKOI MO E VAO MAPA TALAPEANGALI MO E HA’OFANGA MATE’ANGA ‘O E TUI LAVALAVA \n\n TE U LUVA ‘A E SIALE KAKO ‘I HE TUMUTUMU E ‘OFA LOTO NGANGATU ‘ALAHA ‘I HE PĀONGO AKE’ANGA ‘O E TONGA HIHIFO HE MATANGI ‘O E SIU’A’ALO \n\n TAU KO E ONGO ‘A E VAI-KO-LATAI TALAKAIPAU MO FINEFEKAI TA’AHINE PE’I TALAANGE AI KO HO’OTA IA E SAULAVANI ‘O KA NUNU E FANGA-‘O-TAHA NI PEA LOKA ‘A E UAFU-KO-VUNA PEA FASI ‘A FOTU’AFINEMĀ KO E MANAMANA E TAULANGA VŪ’ANGA ‘O E ‘UHINGA LEA ‘EVA’ANGA ‘O E FOLAU TALĀ"),
    Book1(
        number: "118",
        title: 'MATANGI NE AKE MEI HE MOANA',
        content:
            "MATANGI NE AKE MEI HE MOANA LANGA’IA ‘ETE ‘OFA MĀFANA KI HE FU’U HEILALA KO LAPAHA PEA MO E HUNI ‘O MATAMOANA FIO’ANGA IA ‘O E UAINE KAKALA SI’OTO KAHOA FAKAVAHAMAPA PEFIUME ‘O E HALA SIALETAFA ‘OKU MANONGI HE FAKANGALONGATA’A \n\n FO’I FĀHINA KUO VAHA KE HOPO SEI ULA ‘O TEINE VAHA’AKOLO ‘OKU POLOMINATE ‘O KA NUNU ‘A E SOLO TA’ENGALI KUO HOLO AI SI’ETE NOFO PAKIMOETO’I, ‘AHOPANILOLO ‘O KA FELUTENI ‘ILO ‘E HE POTO KO E ‘ESENISI ‘O E ‘OFA LOLOTO ‘OKU KASIKETI PEA TAPU KE TO’O \n\n LAUMANU HINA ‘O E LIKUTAPU NA’E SIUTAKA HE VAHA FOLAU MĀNOA HE SISI FAKAVAINIAKU MAFUA HE FĀ ‘I LONGO’AKAU KO HONO TU’UNGA FAKATALUTALU ‘OKU ‘ILO MEI LONGOLONGOFOLAU TAUKAPA’ANGA ‘O E KANIVATU KO E MANUSIU E HALA UAFU \n\n TAU ‘OIAUĒ KO HAI ‘E LEA KO HAI ‘E LAU KI HE ALAMEA KO SI’I KAKALA NI TE U LAVAME’A ‘E ‘IKAI HU’I PE LUVA HA ME’A"),
    Book1(
        number: "119",
        title: 'TANGITANGI',
        content:
            "SI’I LE’O E PEAU ‘OKU LEA HE FATAI KO E VIKI E LA’A ‘OKU TO EFIAFI PEA LAVE FAKANONGA KO E VAIKAU’AKI ‘I HE NOFO ‘I LELENGA ‘A SI’ETE ‘AMANAKI \n\n LONGO E TU’UAPO ‘O MALU HONO ‘EA PEA KAMO E FETU’U UI HOTO KAUME’A SI’I FEOHI FIEFIA TAKAUA ‘O ‘A’EVA TUKU IA ‘I HE LOTO KO E SISI TAULEVA \n\n LEA ‘A E FANGUFANGU FAKALAU ‘A E ATA ‘O FAFANA ‘A E ‘OFA ‘OKU TOKA MO E KAFA LANGA ‘E TE FAKA’AMU ‘O KA FOTU E MAAMA KI HE MANU ‘OKU PUNA OFI SI’ENE MAMATA \n\n TAU TANGITANGI E KAKALA FEKITA MO E HAHAU UHU SI’OTO MAHENI TOLI FISI MO E LAU HE VAASI HA SILIVA ‘ALAHA KIATE AU TU’U ‘I HE ‘OLITA TEMIPALE E MANATU"),
    Book1(
        number: "120",
        title: '‘OFA MEI PELEHAKE',
        content:
            "MATANGI AKE MATANGI HIFO ‘O FEPAKI TU’U ‘I HOKU LOTO HE SIALETAFA HE SIALEMOTO HEILALA ‘ANAU MAMA’O NE LONGO Ē TAU-‘A-LUPEOKO TAVAKE NI KUO SIU MAMA’O \n\n FANGA ’I HE SĪ LEA’AETOHI KE TALIA MAI Ē MĀPUHOI HE LOLOMA ‘A E FALA MOHOKOI ‘I HE TOKELAU KUO LĪPOI VETEKINA ‘A E KAKALA MANONGI KO HA’ATA IA PAASIPOOTI \n\n HE TU’U TAUFA ‘A E FANGATAPU KUO FAILĀ E LOMIPEAU TAUMU’A KI HE LOTO NEIAFU POUONO MO E SIATE FOLAU KAU NOFO PE ‘O TATALI ATU LAUAKI PŌ SI’ETE MANATU \n\n TAU ‘ISE’ISA Ē ‘ETE FANONGO MAMAHI E TAPU KA KUO ‘ILO KUO NOFO ENI KO E FE’AO PEA UHU ‘O HANGĒ HA KAFO"),
    Book1(
        number: "121",
        title: 'FUIVA, UASI ‘O E FANGATAPU',
        content:
            "NGĀ ‘A E FUIVA UASI ‘O E FANGATAPU TAMAIKI KUO TAIMI Ē FOLAU FILI AI PE MU’A KOFĒ HO SALUSALU KATA ‘EVA HALA TAUFA’AHAU LATA ‘A E SINONI MO E FAKAKAUKAU KI HE TUIPALESI ‘O E HALA UAFU SISI TAULEVA AI PE SI’EKU MANATU HALA PAINI ‘OKA TŌ MAI E HAHAU \n\n KAVEA ‘A E LOTO ‘OKA LEA ‘A E ANGITOA TALI ‘EVA E SOLA KI NUKU’ALOFA FAITEUNGA ‘I HE HUNI PEA MO E FALAHOLA ‘ALAHA NGANGATU AI SI’ETE ‘OFA SEUKE KA MAMA’O PEA FOTU E FAKANONOA TALI KE SIO PEA HOLI FANONGOA MA’ALI ‘A E UINI ANGI ‘I HE VAHANOA ‘O FALALA MAI KAU ‘EVA ‘I HE NONGA"),
    Book1(
        number: "122",
        title: '‘OFA MEI MULI ‘A FUSIPALA',
        content:
            "‘ETE MANATU HE ‘ALU ‘O TOLI VAO KAKALA MATALA MANONGI FETELENI ‘EA PONGIPONGI NE LAVE ‘O NGANGATU OFI TUHA PĒ HUNI MO E HEILALA MAKINOLIA SI’ENE MATALA LATA Ē LOTO NI POTATALA ‘EA E MATANGI TAKA \n\n SI’I FONONGA NA’E TELEKAKI ‘I HE TA’AU ‘O E LELELANGI HE FALAMAO ‘O E TASIMANI FAKALELU E ‘ATAMAI ‘AUĒ KE PUNA E LOFA ‘AVEA AI ‘A SI’ETE ‘OFA HE MALUMALU ‘O E ANGITOA NUNU’ANGA E FINE TONGA \n\n TAU ‘AMUSIA PĒ ‘A E LONGOVUKA ‘OKU PUNA HE FISI ‘O E NAUA ‘O VA’INGA ‘I HE MAHA MO E HU’A FAKAFE’AO HE FANGATAPU NA"),
    Book1(
        number: "123",
        title: 'HĀ’ELE KI PILITANIA',
        content:
            "MATA ME’A FO’OUA SIOFIA E MASANI MĀTANGA E FONUA MO E HALA HE KANALI VAI TŌ HE MO’UNGA MO E ‘AKAU HE TELE’A FAKALELU NE HOUNGA KI HE LOTO KĀVEA \n\n KO E MATANGI KEHEKEHE ANGI HE ‘OTU TAHI NA KO E TALANOA ‘EKE ‘O HA’ATE FIEFIA TALA HONO FAKAOFO SI’I LE’O ‘O NATULA ‘OKU ONGO HE LOLOTO KO HOTO PĀLETU’A \n\n A’USIA ‘ETE ‘ALU KOLOMU’A E MAAMA KO E FAKATALUTALU NE NGANGATU HONO TALA SETI’ANGA SIUELI TAPA’AHO MO E PŌ KALAUNI ‘O E ‘OFA KO SI’OTO MANAKO \n\n KOLO ‘O E FAKAUŌ ‘UTA’ANGA FIEFIA SI’I TALONI E MELINO MO E ANO ‘I SINIVA TUKU Ē TAPUAKI LAU AI ‘ETE MĀLŌ FOKI ‘I HE ‘ALOVILI KI HE VAHA MAMA’O \n\n TAU FIE LAU SI’A IPU NE FONU MAHUOHUA ‘O TATAU AI PĒ HE LA’Ā MO E ‘UHA KAEKEHE NE TAKUA ‘A E ‘OTU FELENITE ‘I HE LANGI ‘OKU NUNU AI E PALANITE"),
    Book1(
        number: "124",
        title: 'SIUELI ‘O E PASIFIKI',
        content:
            "LAVE MĀLIE MAI MATANGI TAULUA TALANOA HE MASANI MO E SETI ‘O SUVA LOTO LEVA KE U HOPO ‘O FAKAFOLAUA KE U LUVA KI HA ADI ‘A SI’OTO LEIPUA PANGO Ē MANATU KUO FAKATU’A TAFESINO’IVAI ‘EA ‘O E HALA VUNA SAMIPENI FIEFIA KO SI’ETE KOULA HANGE SI’A ‘OFA ‘OKU KEI TALAMUKA \n\n KO E LAULŌTAHA TAULANGA PAPE’ETE ‘A TAHISI KEI TAKU KO E SOSAIETE KO E HAUHAU TOFE TUKU KO ‘ENAU PELE KE TE HEKASIA MAI ‘A HONO MATANGI LELE KANEONGO IA KUO FAKATAUELE ‘A E ‘OFA ‘OKU TAPU KE TOE MOVETE ‘I HE ‘OLIVE ‘OKU KAPA TU’U MALELE KANOKATO E LELEI ‘OKU ONGO KEHEKEHE \n\n LETIŌ SASALA ‘A E ONGO ‘O ‘APIA TAPA ‘I HE PO’ULI ‘A HONO KASA ‘UHILA TUHA ‘O E TAFITONGA PŌ NE FETU’U’IA FIE’EVA ‘I HE SOLO MATA ’O NI SIVA \n\n KAE MELE KUO TAUNGASINO ‘IATE KITA ‘A SI’OTO LEI HUNI FIO MO E FĀHINA MUIMUI HOKU LOTO KA HOPO E MAHINA ‘A NUKU’ALOFA MO E VEILI SILIVA \n\n FOLA ‘I HE NUSIPEPA TALA ‘O HAUAI’I FAKALATA ‘A E HULA HIVA ‘ENE VANAIKI KO E FĀNIFO’ANGA ‘O E FILIVAIHI ‘ISE’ISA HOTO LOTO FIE LAKA HE TAIMI KA KUO LĪ TAULA ‘A ‘OFA ‘ALOVILI VE’EVE’E HEILALA KO HA ‘INISENISI VETEKINA AI SI’I ‘ATAMAI ‘OKU FIHI SAI PE SI’I TONGA SIUELI E PASIFIKI"),
    Book1(
        number: "125",
        title: 'MUNILAITI ‘I VAVA’U',
        content:
            "HE MĀHINA NA’E ‘AHO FAKAUA TO’OA HO TANGI LOTO PEA MO’UA HANGE HA TAU MALUMU MEI TU’A ‘A SI’I ‘ATA ‘O E FONUA MO’UNGA \n\n ‘AHO TAHA HE MAHINA FEKITE HULUNGIA AI ‘A MATANGI MALIE TANGI E MANATU ‘IKAI ME’ITE SIA KO KAFOA MO HONO MAILE \n\n SI’A FINE TAU LIKU ‘I MATOTO ‘AMUSIA KO E HOLO HO’O NOFO FE’AO MO E MAHINA ‘ENE HOPO HE MALU PEA TOFU KAE SILONGO \n\n TAU MALAMA E MAAMA HE FONUA HO’ATA MAI LEVA ‘A E PO HOLO’ANGA ‘O E ‘EVA MO E HUA LATA’ANGA IA ‘O SI’ETE NOFO"),
    Book1(
        number: "126",
        title: '‘ETE LATANOA HE HIFO MO E HAKE',
        content:
            "‘ETE LATANOA HE HIFO MO E HAKE EFIAFI PEA MATALA ‘A E SIALE FĀHINA TAUKEI MATANGIMĀLIE KE KE TAKI ATU MU’A NA’A TE HE \n\n TAPA ‘A E TUPA SIO’ATA MEI HE NGŪ HOPO E LA’Ā HE ‘AHO MALŪ SIOFIA MĀTANGA HOLOAKI E VIŪ HE FILIMI ’ETE FAKAKAUKAU \n\n TAU ‘ISE’ISA, KUO HUNI E LOLO ‘O MAUSA HE FUNGA VEITATALO KI HE HEILALA FAKAFE’AO PEA KUO HOLO SI’ETE NOFO HIFOFUA KUO VEUVEU SŌ NAKITA FOLAU ‘A E HINGANO PE’I NOFO Ā KA MAU SOKO KI NUKU’ALOFA MO E FAKAUŌ"),
    Book1(
        number: "127",
        title: 'MANU-‘O-PALATAISI',
        content:
            "FOTU ‘I HE LA’Ā TAPA ‘I HE LANGI MA’A FINE TONGA ‘OKU HAKA PEA TOLI HE KAKALA ‘O LUVA KIA VŪLANGI ‘OKA MALU EFIAFI AKE E MANATU AI ‘ALU KAE FOKI MAI \n\n SI’I FĀ KUO HOPO ‘O TAULUA HOKU LOTO HOTO LEI MOTELOLO LATA’ANGA ‘O E NOFO ANGI ‘A E TOKELAU PEA MOMO E PEAU ‘O FAFANA KIA AU KAVEINGA E FOLAU \n\n FAKALAU ‘A E ATA PEA HOLO E MAMATA KI HE HUNI MO E MAPA ‘OKETI E MATALA KUO ‘EVA ‘I MOANA SI’ETE ‘OFA MAFANA HE ‘UMATA FAKALAVA ‘ASI HE TAUKAKAPA \n\n TAU SI’I PUA ‘ENE FISI ‘O NGAOFE HE UINI LAUKAU ‘O MAHIKI MANU-‘O-PALATAISI TU’U HIFO MU’A HE MALU ‘O E PAAME VA’INGA ‘I HE SIALE SEI ‘OHA TAKA PĒ"),
    Book1(
        number: "128",
        title: 'SI’I MATANGI KUO MĀ’ILI NOA',
        content:
            "SI’I MATANGI KUO MĀ’ILI NOA KATA LOTO TAUKEI NE ‘ILOA TŌ’ONGA ‘O LOTO NUKU’ALOFA ‘UTA’ANGA SI’A FINE LEIOLA \n\n ‘O KA LĪ SI’O LEI KA KE ‘ALU KA E TUKU AI ‘A HO’O MANATU KE FE’AO MO SI’OTO SALUSALU ‘OKU TUKU KO E TALITALI ATU \n\n TU’ILAVONI PE’I KE TALA SI’OKU ‘OFA KI HE UAFU-KO-VUNA Ē HE KI TONGA FAKA’AMU KEU OFI KAU NONGA KEU MISI KI SI’ETA TŌ’ONGA \n\n SI’OTO MAHENI NE VETENGATA’A KA HU’I FETONGI HONAU KAKALA NEONGO E LAU MAILE ‘A E VAHA KAE KITE LOTO SI’OTO MAMANA \n\n TAU PE’I TAU ‘EVA ‘OKA MALUMALU EFIAFI ‘O FANONGO HE HUA FELĀFOAKI ‘OIAUĒ, LAU MAI Ē MOKOMOKO PĒ ‘OTU FELENITE"),
    Book1(
        number: "129",
        title: 'HIVA AFĀ ‘A VAEA: FANGA-I-LIFUKA',
        content:
            "HE SIKA-‘A-HEMA FAKAHIHIFO LOULOUA’A Ē MATANGI TŌ KOLONGATATA NE FAKAUŌ ‘O FALALA ‘I FAKA’OSIKATO ‘ISE’ISA E KAO MO TOFUA VILINGIA HOTO KIE TĀUA KUO HOLA ‘A E FANGA-‘I-LIFUKA TOUNOA E MAKA KO LOUPUA \n\n PĀ ‘A E UI MO E FIE UŌ OFO ‘A E FANGA-FALIKI-PAKO FINE HA’APAI KE FAKAFANONGO UI ‘A E FANGUFANGU ‘I HE PŌ FAKAMAFOLA ‘I HE VAHA MAMA’O KO SI’OTO SALUSALU MANAKO KE TALIA ‘A E ANGI NA’A TŌ KE ME’ITE SIA KO VEIONGO \n\n KA FEHU’I ‘E HE FUTU KO VUNA \n\n PE ‘EKEA ‘E HE TAUNGA PEKA TAUTAVALE PE‘I KE TALA HE KOAU E KO E TALATĀ KO E MANUSIU ‘O UOLEVA KO SI’EKU ME’AKAI KO E FĀ ‘O INU HAHAU MO KAI LA’Ā MOHENGA ‘I HE FISI ‘O E NAUA \n\n TAU LĒLEA Ē HOKU ‘OFA ATU SIA KO KAFOA ‘ALO ‘I TALAU TANGI ‘A E TOA TU’UAKITAU HE KUO FE’OFA’AKI ‘A KAKAU FASI PE SILA TUKU KI MANONO KE FANGU MALINGI ‘I VAHA’AKOLO NE NGANGATU ‘O ‘ALAHA ‘I HE LOTO KAE TUPULAKI ‘OFA ‘I HE MOTO KA FAINGATA’A PE FAINGOFUA HA’APAI, TU’U HO’O KAI MU’A"),
    Book1(
        number: "130",
        title: 'LEA ‘A E FUIVA',
        content:
            "ALAMUHU E TOA HE FANGA FETAU FAKANUNU E MATANGI TOKELAU KI HA FINE TEITI ‘I HE HALA UAFU MATA MĀHINA SI’ENE KAKAU TAUTAUKAPA AI E FISI’I PEAU HANGE HA SISI KUO FAKAVAINIAKU \n\n KA UŌUŌ LEVA ‘A E HALA KO VUNA ‘O KAINAKEHEA MO E HALA TAUFA TOKI HU’I AI HOTO FAKAMANULUA KE KAHOA HA TAHA FOLAU MEI TU’A KAE MOLE AI ‘ENE FAKA’UTO’UTA ‘I HE TUI FIO ‘A E FĀHINA E LEI PUA \n\n TAMAIKI LAKA TA’OTA’OFI PE KAE TUKU KE U TOLI E MOTO E SIALE KE FUNGANI’AKI ‘A E SISI MAILE KO HOKU TEUNGA KI HE FAKATĒTĒ KE HAU’ATEA MO TALI TUEE ‘O MAMATA KAE HOPO E PELE \n\n TAU ‘OIAUĒ KO E NGALU E FASI TA’AHINE TALI KE MATALI HOPO E FĀ TALAPEANGALI ME’A KUO MASENI AI"),
    Book1(
        number: "131",
        title: 'SEI ‘O FAFINE',
        content:
            "FANGUNA E HUNI HE TAHI UHU SI’ONO MATALA NE ANGA MALU ‘I HE INUMIA ‘A E HAHAU ‘O KA TOULIKI HA PŌ MALU UE’I E LOTO KEI TAKA TU’U KE TOLI FUNGANI ‘I HA’ANE TEU \n\n TUI MAPALEI TUI MAPAFISI KO E FAKAVAHA E KAKALA FILI NE TUKUA MAI ‘I HA ANGIIKI PEA TUPU AI Ē TALAFILI ‘EVA ‘I PULOTU PE HA MOHE MISI \n\n ‘A E TALANOA ‘OKU NGANAIKI \n\n FAKAPŌ HOTO SISI HEILALA NE POTO HE TALANOA FANAFANA KI HA ‘ATAMAI KEI VA’AVA’A TE’EKI KE PAU HANO MAMANA NE U TAKU KO E LAULŌTAHA HE VAHA’A ‘O E FEFINE MO E TANGATA \n\n TAU ‘A HA! ‘E HĒ! ‘A E SEI ‘O FAFINE LAUKAU AI PĒ MU’A KAU TULI VAHA’A FONUA"),
    Book1(
        number: "132",
        title: 'TA’ANE MĀHANGA ',
        content:
            "KE TULOU MO E HEILALA NA’E TUITU’U PEA LAVALAVA TU’U TAUHOA MEI MA’ANANGA PEA FAKAMA’U KI AI E TALA ‘A HOU’EIKI MO E KAINGA \n\n HE MATANGI TŌ HE FUNGA ‘ALAKI PEA HALA HE FAKASIUMALAKI ‘ISE’ISA Ē PEHEANGE MAI ‘ILO ‘E HE TOA MAVAE TANGI HOTO NĀPA’A LILO ‘OKU FAI \n\n TOKELAU ‘O HAKO ‘I MANAVANGA PEA LOKA ‘A E FAKAHĒ VAKA LELU AI ‘EKU MANATU NI TAMA KI HE ANGA ‘O TA’AKIMOEAKA ‘OKU TUKU LOTO KO E ‘OFA’ANGA \n\n TAU KUO LANGA SI’ETE MANATU NOA SIUTAKA ‘A E MANU KO E LOFA KO E NAVIKEITA ‘O E VAHANOA ‘OKU MĀNOA ‘I HE FALAHOLA HENGIHENGI KAE ‘ALA ‘I VAILAHI FAITEUNGA E HUNI MO E LANGAKALI KO E TOKI LUVA HE VUNGA-MO E-AHI KO E ME’A KUO MAHENI AI"),
    Book1(
        number: "133",
        title: 'NEPITUNO',
        content:
            "NEPITUNO FAI HA’O MALELENGA KUO U FANONGO HE TALAMAHE’A TĀ NE KOLOSI ‘A E ‘EKUETA ‘E HE HAU TU’U FAKALEVELEVA KO E SIU FAKAHĀKONOA ‘A E TAULUA TAVAKEOMA ‘O FEKUMI KI HE ‘API MOANA ‘O E MATAKALI POLINISIA \n\n MAFOLA E PASIFIKI SAUTE HONO ‘OTU MOTU NE LAU AFE KA NE FAKAKŪ PEA ‘ANEFĒ \n\n KALAUNI SOVALENI NE AFE ‘EVA HE MATANGI FAKATĒTĒ PEA MO E ‘EA E VAHA MOHE HE KO E AFENGA FAKAME’ITE LATA’ANGA ‘O E MANU TA’ANE \n\n ‘E HINA MO SINILAU KE HAKE ‘I HE FANGA E ‘OTU FELENITE TAMA KO TANGITA KO E PĀ Ē ‘OKU KEI TU’U MO E LAU PE KUO LAHI ‘A E FOLAU MOLE HE TAUTEFUA ‘A NIUMOTU’U Ē ‘O FAKAMO’ONI HE ‘AHO KO EE ‘A E TEPI FAKANGALI FEFINE \n\n TAU HE TUETUĒ KAE FĒFĒ KO E HĀKAILANGITAU ‘ANGA Ē ‘OFA KE TOLONGA ‘O TUPUTUPU’A ‘A E HAU ‘OKU MALU‘I FONUA"),
    Book1(
        number: "134",
        title: 'TALA ‘O LO’AU',
        content:
            "TUKUANGE PĒ TALA ‘O LO’AU KI HE FAKAFEANGAI ‘O E HAU HEHENGA SI’ETE FAKAKAUKAU ‘O FEKUMI NA’A MA’U HA FEAU ‘O E LOTO KUO KAFO HE MANATU HE ‘AHO NI MO HONO FEINGATAU x2 \n\n PILOLEVU Ē SIU’ILIKUTAPU TO’O UALULU MO E LAULAU HA’OFIA ‘A E HEMA MO MATA’U KANO E HALA TUKU KIA AU KA LĪ HA PULU PEA HAPO ATU \n\n HE KO ‘EKU KAI FAKATALUTALU x2 \n\n NAFUALU Ē HOKO MO TOLOA ‘A E HIKIHIKI ATU E FONONGA KUMI E MAFUA TUHA E KUONGA KA TE NOFO PĒ ‘O LE’O KOLOA FETU’UTAKINGA ‘O NUKU’ALOFA NUNU’ANGA ‘O E KAKALA HINGOA x2 \n\n KO E SĪTENGA ‘A E FEFINE FAKA’ĀNAU KI HĒ MO Ē KIA HIKULE’O MO HONO FALE KO E IVI ‘O FAIMĀLIE KE FETAKINIMA HE ‘AHO KO Ē TU’U AI E PALĀTAVAKE x2 \n\n TAU HAKE ‘A TAHI NA ‘O HIFO MO ‘UTA ‘EVA HE LAKA ‘O MATAFONUA FUIFUI AFE ‘I HALAEVALU FOTUAKI MAI E TA’U TEAU ‘ISE’ISA Ē ‘ETE MĀFANA TUKUPĀNGINA E FAKAHUAFA ‘OFA HOTO SISI FAKAMANULUA ‘OKU TUKU KO’ETE FAKAMA’UNGA"),
    Book1(
        number: "135",
        title: 'HIVA ‘O MAKAHOKOVALU',
        content:
            "TOKELAU NA’E ANGI PEA HAKO ‘O FALALA HE FUNGA NIUTAO LU’IA HONO MAPO’I HINGANO NA’E MOTO ‘I ‘OUA-NA’A-NGALO \n\n ‘ĀHEA MO E FĀ ‘I HE TUVA VAKAIA SI’I FETU’U KOULA KUO FAKAPULI ‘I HE FUTU-KO-VUNA NA’E NGANA MO E MALU’I FONUA \n\n ‘E FANGA SIALE KO E TAU Ē KUO LAPA ‘A E HIKUTAMOLE ‘I HE FĀHINA E PAEPAE KO SI’I MAUSA ‘O E FANGATAVE \n\n HE KO LELEA TALĀ E VAI HAO’ULI AI ‘A E TOUTAI TE ‘OFA HE FĀFĀ-KONGA-TAHI TUKUTALA ‘OKA NUNU ‘A PANGAI \n\n TAU HE SIU NA KUO ‘ALAIMOANA TOUNOA SI’I LALA HEILALA ‘ALO-‘I-TALAU KO E MĀTANGA KUO FAI AI ‘APĒ HA LATA SEI ‘O FAFINE ‘EVA HE MAHENI HOTO PALE MOTOMOTO’I LEI KO E LOA NA’E TU’U HENGIHENGI KO E KAPAKAU LĒVENI"),
    Book1(
        number: "136",
        title: 'HIVA ‘O SIAOSI ‘ULUKIVAIOLA',
        content:
            "FAKA’ISE’ISA E MATANGI TONGA LOLOMA E TOKELAU HAKONOA MAUMAU E ‘ELILI MO E LOKA ‘I HE PULI ‘A E KAKALA ‘AONGA \n\n ‘OHOVALE ‘A PALUKI PEA HANU UAFU-KO-VUNA, HĀ HA’O LAU NA’E VETE FĒFĒ FAKAMA’U ‘O E KIE FOKOLOLO-‘O E-HA’U \n\n MALELE ‘ŌVAVA ‘O FANGASIALE MOTU E MAEA ‘A TOLUAFE HE SAIKOLONE NE SIAKALE VILINGIA E PAASI ‘A PAILATE \n\n FAKA’OSI FAKA’ATA E LA’AA VĀ ‘O VAVA’U PEA MO TONGA NA’E HĀ AI HONO TANGATA HAOFAKI ‘O E HIFOFUA \n\n ‘OKU TALA PĒ ‘A E ‘UHINGA HE FONUALOTO KI HE VAKA TU’UTAI E FAIFATONGIA FUA TUKU’ULU HE KĀPASA \n\n TAU ‘E ULAMOLEKA MO E TAUTAHI ‘E’AKAU’OLA, KE TALIA MAI PE KO E AVA NI ‘E PUNI ‘E HAI ‘I HE TŌ TAU ‘A E LAFITANI HĀ ‘A E SINO ‘O E MATEAKI NA’E TĀ TUKU PĒ HE UASI"),
    Book1(
        number: "137",
        title: 'HUA KEI VEVELE ',
        content:
            "‘ATA ‘O E HAI KOULA NE LALANGA PEA U KAHOA ’OU TAVANI PEA U NO’OPONA KA E TA KOAA KUO VETE NGOFUA IKU’ANGA ‘O SI’EKU TAU FĀ FELUTENI KAE NOFO PAEA \n\n KA KO’ENA ‘A E TOHI ‘O E FUAKAVA MO HOTA MAAMA KOULA SAFAILA TANGI E KAKALA ‘OKA EFIAFI PEA ANGIANGI IKI E MATANGI HANGĒ HA ‘OFA ‘OKU TULIMUI MAI KAE TĀ KOAA KUO SIVA ‘ETE ‘AMANAKI AOTEAROA KUO NGALO NGATA’A FAUNITENI KO E LO’IMATA MO E MATAVAI ‘O ‘ETE LOTO HOHA’A UISA Ē KUO ‘IKAI MALAVA PŌNGIA E LOTO NI HE MĀVAE HO’O FOLAU NE TU’UPAKEE HAKE E PEAU PEA KUO NE ‘AVE SI’OTA HUA NA’E KEI VEVELE ‘ATU E MANATU KO HO TU’AFALA KE KUMIFONUA KI HO SINO NA KA KO’ENA E KAKALA KUO TAULEVA KE TOKI VETE ‘E FA’ITOKA"),
    Book1(
        number: "138",
        title: 'TUAIKAEPAU',
        content:
            "VAKAI HE TOHI MĀHINA, FĀ ‘O E FITU E ONO UA FOLAU NA KUO FUSI TAULA, ‘I HE TAULANGA NUKU’ALOFA TU’UFUA AI E KĀPASA, FAKATELE HE MAKA-O-‘OA KAE FATU SI’ONO TAUMU’A, TOFIKULU E ‘AOTEALOA SEUKE, TĀPINGA ‘A MAAMA, KO ‘ETA FONONGA TA’E’ILOA \n\n HAKAU MINEVA PE’I FAKAHĀ, HO’O PUKE HUELO E MĀHINA LAVAKI’I ‘A E FOLAU ‘EVA, KE HU’I HONAU SISI KAKALA KA KE LAUKAU FIE TANGATA, KI HE VAKA TOUTAI E LA’Ā ‘OKU FAKA’OLUNGA ‘I HO NIMA, KAE KAFU’AKI E PEAU TĀ \n\n SI’I E FO’OU, SI’I E SOLA, HE MO’UNGA E LOTO’I TONGA \n\n TUKU KE ‘ALU E FAINGATA’A, HE’IKAI LAVA FAKAMATALA ‘A E MAMAHI MO E HA’AHA’A, ‘AMANAKI NA’E FONU TĀLA’A HE TOLU MAHINA E SIO VAHA, ‘IKAI MA’ALI HA KALOFIAMA TU’UHOLOAKI E MASI ‘E TAHA, KE NE PUKEPUKE MAI E MAAMA NE ‘EKO ‘I MĀMANI E MAVAVA, HE KO E IKUNA KUO KAFAKAFA \n\n ‘ALO E MATANGI FA’AKI E LOA, FO’UVAKA PE HANGĒ KO NOA ‘AKI ‘A E TUI KO ‘ETA KOLOA, FUSI E KOLOSI FAKATAU ‘AU TOKA’ONE HE MOTU KO KATAVU TULOU MO SUVA, ‘ISA Ē, HALA VUNA S.O.S ‘A E MĀTU’A, LAUTALA PEI, ‘E NGALO ‘AFĒ HO’O LAULĀ NE FAKALAVE, FAKAFOKI MAI ‘A E ‘AUHĒ \n\n TAU TAMA TONGA PE’I TULI KAVEINGA TAUMU’A KI HE FETU’U NGINGILA TAMA TONGA, MA’U PĒ HO LOTO KE LAU E TAUMU’A NGAHOLO TAMA TONGA E FUSI A’U HO’O FUKA ‘ULITU’U KI HE MOTO E FONUA"),
    Book1(
        number: "139",
        title: 'HAKOHAKO',
        content:
            "HAKOHAKO ‘A E TONGA HAHAKE PĀPANI ‘I HE MOTU KO ‘ONE UE’IA Ē NGALU-’A-FAFINE AFUHIA ‘A E MAKAMAILE ‘O VA’INGA HE NĪKAU PAAME ‘OKU LE’O ‘I HE LOTO NGOUE ‘O FANONGO ‘I HE VĀVĀKEE HOUA E PASIFIKI SAUTE \n\n KAUPŌ’ULI E TAFITONGA \n\n NUNU E PANGAI-‘O-TANGATA HE FAKAMAHENI’ANGA E SOLA ‘OKU ‘EVA TALANOA HE FANGA KUINI SALOTE ‘A E UAFU TALĀ ‘OKA NO’O ‘A E FOLAU VAKA KAE HEKA ‘A E SISI ‘UHILA FAI FOKI PĒ TE U FIU HE HĀ \n\n UISA E MANATU KUO FAKALOLOMA TULUTĀ E ‘OFA KO E LAVENIUOTA PĒFIUME ‘O E KAKALA HINGOA ‘O ‘ALAHA KA MALŪ TOKA KAKALA MO E HINGANO FAKALATA TOLI MOTO ‘I KAUHALA ‘UTA ‘O KUMUNI KO E MATA’IKOLOA LAUKAU’ANGA ‘A E FANGALONGONOA \n\n TAU OLO E LUPE ‘OKA EFIAFI TAUKAPA ‘ENE FAKAMANIMANI HEILALA ‘OKU KEI TANGITANGI HONO UE’I ‘E HE MATANGI TONU E ‘UTA TALA PEA NGALI NONGA E LOTO MO E ‘ATAMAI"),
    Book1(
        number: "140",
        title: 'HIVA ‘O RATU ‘ETUATE',
        content:
            "FAFANA Ē TONGA KI HE TOKELAU ONGO ‘I HE NGALU’EA MO E PEAU HOLO ‘A MO’UNGA ‘O LOMANIPAU LAUTELE E FOLAU SULUNGA KE A’U LOULOUA’A E HEILALA FANGATAPU KI HE VUNIVALU MO E KAHA’U \n\n KUO TŌ Ē KAKAPU HE FUNGA FONUA \n\n TU’A ‘OFA ‘I HE ‘AHO ‘O E FONONGA PEA TEKI ‘A E SOLA ‘O MAMATA FAKAPŌ HE ‘UMATA NE FAKALAVA HE KAU PEAU ‘O E RAMARAMA UISA Ē PE KO HAI TE NE LAVA \n\n ‘A E SONGO LATA ‘ISA KO E HA Ē ‘A E SĪLONGO ‘A SI’EKU PELE ‘I HE ‘AHO ‘O E HAKE KI FATANITAVAKE TU’I NĒKETI FAI HA’O PULE ‘E NAILASIKAU KUO FĒFĒ MOTU Ē MAEA ‘O TAHA KAE AFE \n\n TAU UISA RATU E ‘O LOTO BAU ‘A E KAKALA NI KA MELIELAU KAPAKAISI KI HE ‘ALO ‘I TALAU TAU’AKIPULU MO TU’UAKI TAU FOTU-‘A-FINEMĀ Ē TALAKAEPAU KO KITA NI ‘APĒ KUO TŌ TAU"),
    Book1(
        number: "141",
        title: 'HUNI MO E MAPA',
        content:
            "KO E HUNI PEA MO E MAPA PEA MO E TANGITANGI ‘O LOTO LAPAHA NA’E TUIAKI KO HONO KAKALA FAKA’OTULANGI PEA MO E HEILALA \n\n KO E FALAHOLA MO E MO’ONIA \n\n TUIA HE PAPAI FAKA’OTUSIA VĀVĀKĒ HE HAUHAU VUNA ‘EVA’ANGA IA ‘O E LAUMANU HINA \n\n SI’I MAILE NA’E FIHIAKI MALIU HE HIKUHIKU MATANGI ‘I HE FALAMAO ‘O E TASIMANI KO E MĀTANGA IA ‘O E LELELANGI \n\n TAU FINE LOTO MU’A PE’I LANGA TOLI ‘I HE KAKALA NA ‘OKU MANONGI LIKUTAPU NE ‘ALAHA PONGIPONGI HANGĒ HA HUA FAI FELOTOI ‘O VEITALASI MO TAKUILAU LATA’ANGA IA ‘O E MANU LAUKAU"),
    Book1(
        number: "142",
        title: 'FUNGANI',
        content:
